import React from "react";
import './style.css';

function Container(props){
    if(props.icon == 'bookshelf')
    return(
        <div className="card shadow mx-auto wrapper">
            <div className="card-header">
                <h4 className="my-1" style={{fontWeight:'bold'}}><i class="fas fa-book"></i>{' '+ props.header}</h4> 
            </div>
            <div className="card-body">
                {props.children}
            </div>
        </div>
    )
    if(props.icon == 'cat')
    return(
        <div className="card shadow mx-auto wrapper">
            <div className="card-header">
                <h4 className="my-1" style={{fontWeight:'bold'}}><i class="fas fa-cat"></i>{' '+ props.header}</h4> 
            </div>
            <div className="card-body">
                {props.children}
            </div>
        </div>
    )

}

export default Container;
