import React from "react";
import './style.css';
// import { Link } from "react-router-dom";

export function BtnSubmit(props){
        return(
            <button className="btn btn-secondary search-btn"  onClick={props.onClick} style={{fontWeight:'bold'}}>
                {props.children}
            </button>
        )
    }

export function BtnView(props){
        return(
            <a className={'btn btn-dark card-btn mr-1'} href={props.link} onClick={props.onClick}  style={{fontWeight:'bold'}} target={props.target}><i className="fab fa-search" />
                {props.children}
            </a>
        )
    }

export function BtnSave(props){
        return(
            <button className={props.isSaved ? ('btn btn-secondary card-btn'):('btn btn-primary card-btn')}  onClick={props.onClick} style={{fontWeight:'bold'}}>
                {props.isSaved?("DELETE"):("SAVE")}
            </button>
        )
    }
